mapper.select:
select nickname, member_id, picture, level
from members
where member_id = #{member_id}

mapper.update:
update members set nickname=#{nickname} where
member_id=
#{member_id}

update members
set password = #{pwdcheck1}
where
member_id = #{member_id}

update members m inner join
chef c
on m.member_id = c.chef
set password = #{pwdcheck1}
where
m.member_id = #{member_id} and c.chef= #{member_id}
