mapper.select:
		select * 
		from recipe_info natural join members 
		where recipe_id in (
					select *
                    from
                    	
                    	(
                    		SELECT recipe_id
							FROM ingredient natural join recipe_ingre
							where ing_id = #{ingre}
                        ) as r#{i} 
                    	
                    	group by r0.recipe_id

                    )

		select * 
		from recipe_info natural join members 
		where recipe_id in (
					select *
                    from
                    	
                    	(
						SELECT recipe_id
						FROM recipe_category natural join category
						where category_id = #{id}
						) as r#{i}
                    	
                    	group by r0.recipe_id

                    )
